import React from 'react';
import {
  View,
  Text,
  Image,
  TouchableOpacity,
  ScrollView,
  FlatList,
  StyleSheet,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons'; // Para o ícone de seta

const products = [
  {
    id: '1',
    name: 'Colar Chapa Oval',
    price: 'R$ 197,00',
    image: require('./assets/product1.png'), // Substitua pelo caminho da imagem
  },
  {
    id: '2',
    name: 'Chocker Durmas',
    price: 'R$ 169,00',
    image: require('./assets/product2.png'), // Substitua pelo caminho da imagem
  },
  {
    id: '3',
    name: 'Colar Mistério Noturno',
    price: 'R$ 234,70',
    image: require('./assets/product3.png'), // Substitua pelo caminho da imagem
  },
  // Adicione mais produtos conforme necessário
];

const CatalogScreen = () => {
  const renderProduct = ({ item }) => (
    <View style={styles.card}>
      <Image source={item.image} style={styles.productImage} />
      <Text style={styles.productName}>{item.name}</Text>
      <Text style={styles.productPrice}>{item.price}</Text>
      <TouchableOpacity style={styles.addButton}>
        <Text style={styles.addButtonText}>Adicionar ao Carrinho</Text>
      </TouchableOpacity>
    </View>
  );

  return (
    <View style={styles.container}>
      {/* Ícone de seta */}
      <Ionicons
        name="arrow-back"
        size={30}
        color="#000000"
        style={styles.backIcon}
      />

      {/* Título */}
      <Text style={styles.title}>Catálogo de Produtos</Text>

      {/* Subtítulo */}
      <Text style={styles.subtitle}>Coleção Primavera 2025</Text>

      {/* Lista de Produtos */}
      <FlatList
        data={products}
        renderItem={renderProduct}
        keyExtractor={(item) => item.id}
        numColumns={2}
        contentContainerStyle={styles.productList}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#EEEDED',
    padding: 13,
  },
  backIcon: {
    position: 'absolute',
    left: 19,
    top: 34,
  },
  title: {
    fontFamily: 'Roboto', // Substitua por 'K2D' após configuração
    fontWeight: '800',
    fontSize: 32,
    lineHeight: 42,
    color: '#000000',
    textAlign: 'center',
    marginTop: 70,
  },
  subtitle: {
    fontFamily: 'Roboto', // Substitua por 'K2D' após configuração
    fontWeight: '300',
    fontSize: 24,
    lineHeight: 31,
    color: '#000000',
    marginTop: 20,
    marginLeft: 75,
  },
  productList: {
    marginTop: 50,
  },
  card: {
    width: 189,
    height: 265,
    backgroundColor: 'rgba(228, 148, 148, 0.27)',
    borderWidth: 1,
    borderColor: '#000000',
    margin: 5,
    alignItems: 'center',
  },
  productImage: {
    width: 187,
    height: 172,
    borderWidth: 1,
    borderColor: '#000000',
  },
  productName: {
    fontFamily: 'Roboto', // Substitua por 'K2D' após configuração
    fontWeight: '800',
    fontSize: 16,
    lineHeight: 21,
    color: '#000000',
    textAlign: 'center',
    marginTop: 10,
  },
  productPrice: {
    fontFamily: 'Roboto', // Substitua por 'K2D' após configuração
    fontWeight: '800',
    fontSize: 16,
    lineHeight: 21,
    color: '#000000',
    textAlign: 'center',
  },
  addButton: {
    width: 187,
    height: 39,
    backgroundColor: '#76BAF2',
    borderWidth: 1,
    borderColor: '#000000',
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: 10,
    transform: [{ rotate: '-0.32deg' }],
  },
  addButtonText: {
    fontFamily: 'Roboto', // Substitua por 'K2D' após configuração
    fontWeight: '800',
    fontSize: 16,
    lineHeight: 21,
    color: '#FFFFFF',
  },
});

export default HomeScreen;
