import React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import HomeScreen from '../screens/HomeScreen';
import ProductDetailsScreen from '../screens/ProductDetailsScreen';
import CartScreen from '../screens/CartScreen';
import CatalogScreen from '../screens/CatalogScreen';

const Stack = createNativeStackNavigator();

const AppNavigator = () => {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Home">
        <Stack.Screen
          name="Home"
          component={HomeScreen}
          options={{ title: 'Loja de Semijoias', headerTitleAlign: 'center' }} // Centraliza o título
        />
        <Stack.Screen
          name="ProductDetails"
          component={ProductDetailsScreen}
          options={{ title: 'Detalhes do Produto', headerTitleAlign: 'center' }} // Centraliza o título
        />
        <Stack.Screen
          name="CatalogScreen"
          component={CatalogScreen}
          options={{ title: 'Detalhes do Produto', headerTitleAlign: 'center' }} // Centraliza o título
        />
        <Stack.Screen
          name="Cart"
          component={CartScreen}
          options={{ title: 'Carrinho', headerTitleAlign: 'center' }} // Centraliza o título
        />
      </Stack.Navigator>
    </NavigationContainer>
  );
};

export default AppNavigator;
