import React, { useCallback, useState } from 'react';
import { FlatList, StyleSheet, View, Text, RefreshControl } from 'react-native';
import ProductCard from '../components/ProductCard';
import { products, Product } from '../data/products';
import { NativeStackNavigationProp } from '@react-navigation/native-stack';


// type HomeScreenNavigationProp = NativeStackNavigationProp<RootStackParamList, 'Home'>;


const HomeScreen = ({ navigation }) => {
  const [refreshing, setRefreshing] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  // Simula carregamento inicial
  React.useEffect(() => {
    setTimeout(() => setIsLoading(false), 1000); // Simula fetch de dados
  }, []);

  // Função para pull-to-refresh
  const onRefresh = useCallback(() => {
    setRefreshing(true);
    setTimeout(() => {
      setRefreshing(false);
    }, 1000);
  }, []);

  // Renderização otimizada do item
  const renderItem = useCallback(
    ({ item }) => (
      <ProductCard
        product={item}
        onPress={() => navigation.navigate('ProductDetails', { product: item })}
      />
    ),
    [navigation]
  );

  // Componente para mensagem de lista vazia
  const ListEmptyComponent = () => (
    <View style={styles.emptyContainer}>
      <Text style={styles.emptyText}>Nenhum produto encontrado</Text>
    </View>
  );

  if (isLoading) {
    return (
      <View style={styles.loadingContainer}>
        <Text style={styles.loadingText}>Carregando...</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <FlatList
        data={products}
        keyExtractor={(item) => item.id.toString()}
        renderItem={renderItem}
        contentContainerStyle={styles.listContent}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
        }
        ListEmptyComponent={ListEmptyComponent}
        numColumns={2} // Exibe em grade com 2 colunas
        showsVerticalScrollIndicator={false}
        initialNumToRender={10} // Otimiza performance inicial
        windowSize={21} // Define janela de renderização
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f5f5f5',
  },
  listContent: {
    padding: 10,
  },
  loadingContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#f5f5f5',
  },
  loadingText: {
    fontSize: 16,
    color: '#333',
  },
  emptyContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  emptyText: {
    fontSize: 16,
    color: '#666',
  },
});

export default HomeScreen;
