          source={{ uri: 'https://example.com/logo.png' }} // Substitua pela URL ou require da logo
          style={styles.logo}
        />
        <Text style={styles.title}>Semijoias cer√¢mica & artes</Text>
        <Text style={styles.subtitle}>Beleza artesanal para voc√™ brilhar</Text>
      </View>

      {/* Bot√µes */}
