        <TouchableOpacity style={styles.button} onPress={() => console.log('Cat√°logo')}>
          <Text style={styles.buttonText}>üìã Cat√°logo</Text>
        </TouchableOpacity>

        <TouchableOpacity style={[styles.button, styles.promotionButton]} onPress={() => console.log('Promo√ß√µes')}>
          <Text style={styles.buttonText}>üí∞ Promo√ß√µes</Text>
