  },
  subtitle: {
    color: '#fff',
    fontSize: 16,
    marginTop: 5,
  },
  buttonsContainer: {
    width: '100%',
    alignItems: 'center',
  },
  button: {
    width: '80%',
    paddingVertical: 15,
    borderRadius: 10,
    alignItems: 'center',
    marginVertical: 10,
    backgroundColor: '#f4a261', // Cor do bot√£o Cat√°logo
  },
  promotionButton: {
    backgroundColor: '#a267ac', // Cor do bot√£o Promo√ß√µes
  },
  contactButton: {
    backgroundColor: '#6ab547', // Cor do bot√£o Contato
  },
  buttonText: {
    color: '#fff',
    fontSize: 18,
    fontWeight: 'bold',
  },
  footer: {
    color: '#fff',
    fontSize: 12,
    textAlign: 'center',
    marginBottom: 20,
  },
});

export default HomeScreen;
