import React, { useState } from 'react';
import { FlatList, StyleSheet, View, TextInput, RefreshControl } from 'react-native';
import ProductCard from '../components/ProductCard';
import { products } from '../data/products';

const HomeScreen = ({ navigation }) => {
  const [search, setSearch] = useState('');
  const [refreshing, setRefreshing] = useState(false);

  const filteredProducts = products.filter((product) =>
    product.name.toLowerCase().includes(search.toLowerCase())
  );

  const onRefresh = () => {
    setRefreshing(true);
    // Lógica para atualizar os produtos
    setTimeout(() => {
      setRefreshing(false);
    }, 2000);
  };

  const renderItem = ({ item }) => (
    <ProductCard
      product={item}
      onPress={() => navigation.navigate('ProductDetails', { product: item })}
    />
  );

  const ListEmptyComponent = () => (
    <View style={styles.emptyContainer}>
      <Text style={styles.emptyText}>Nenhum produto encontrado.</Text>
    </View>
  );

  return (
    <View style={styles.container}>
      <FlatList
        data={filteredProducts}
        keyExtractor={(item) => item.id.toString()}
        renderItem={renderItem}
        contentContainerStyle={styles.listContent}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
        }
        ListEmptyComponent={ListEmptyComponent}
        numColumns={2} // Exibe em grade com 2 colunas
        showsVerticalScrollIndicator={false}
        initialNumToRender={10} // Otimiza performance inicial
        windowSize={21} // Define janela de renderização
        columnWrapperStyle={styles.columnWrapper} // Adicionado para centralizar
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f5f5f5',
  },
  listContent: {
    padding: 10,
    alignItems: 'center', // Centraliza os itens verticalmente
    justifyContent: 'center', // Centraliza os itens horizontalmente
  },
  columnWrapper: {
    justifyContent: 'center', // Centraliza as colunas
  },
  loadingContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#f5f5f5',
  },
  loadingText: {
    fontSize: 16,
    color: '#333',
  },
  emptyContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  emptyText: {
    fontSize: 16,
    color: '#666',
  },
});

export default HomeScreen;
