import React from 'react';
import './CatalogScreen.css';
import { addToCart } from './cartLogic';

const CatalogScreen = () => {
  const products = [
    { name: 'Colar Chapa Oval', price: 'R$ 197.00', image: 'chapa-oval.jpg' },
    { name: 'Chocker Durmas', price: 'R$ 169.00', image: 'chocker-durmas.jpg' },
    { name: 'Colar Mistério', price: 'R$ 234.70', image: 'colar-misterio.jpg' },
  ];

  const handleAddToCart = (product) => {
    addToCart(product);
  };

  return (
    <div className="catalog-screen">
      <h1>Catálogo de Produtos</h1>
      <h2>Coleção Primavera 2025</h2>
      <div className="product-list">
        {products.map((product, index) => (
          <div key={index} className="product-item">
            <img src={product.image} alt={product.name} />
            <div className="product-details">
              <h3>{product.name}</h3>
              <p>{product.price}</p>
              <button onClick={() => handleAddToCart(product)}>Adicionar ao Carrinho</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CatalogScreen;
